body {
  line-height: 1;
}
ol,
ul {
  list-style: none;
}
blockquote,
q {
  quotes: none;
}
blockquote:before,
blockquote:after,
q:before,
q:after {
  content: '';
  content: none;
}
table {
  border-collapse: collapse;
  border-spacing: 0;
}
body {
  font-family: 'PT Sans', sans-serif;
  min-height: 740px;
  background: #d7d7d7;
  background: -webkit-gradient(
    radial,
    50% 50%,
    0,
    50% 50%,
    500,
    from(#f0f0f0),
    to(#bebebe)
  );
  background: -webkit-radial-gradient(#f0f0f0, #bebebe);
  background: -moz-radial-gradient(#f0f0f0, #bebebe);
  background: -ms-radial-gradient(#f0f0f0, #bebebe);
  background: -o-radial-gradient(#f0f0f0, #bebebe);
  background: radial-gradient(#f0f0f0, #bebebe);
}
b,
strong {
  font-weight: bold;
}
i,
em {
  font-style: italic;
}
a {
  color: inherit;
  text-decoration: none;
  padding: 0 0.1em;
  background: rgba(255, 255, 255, 0.5);
  text-shadow: -1px -1px 2px rgba(100, 100, 100, 0.9);
  border-radius: 0.2em;
  -webkit-transition: 0.5s;
  -moz-transition: 0.5s;
  -ms-transition: 0.5s;
  -o-transition: 0.5s;
  transition: 0.5s;
}
a:hover,
a:focus {
  background: #fff;
  text-shadow: -1px -1px 2px rgba(100, 100, 100, 0.5);
}
.fallback-message {
  font-family: sans-serif;
  line-height: 1.3;
  width: 780px;
  padding: 10px 10px 0;
  margin: 20px auto;
  border: 1px solid #e4c652;
  border-radius: 10px;
  background: #eedc94;
}
.fallback-message p {
  margin-bottom: 10px;
}
.impress-supported .fallback-message {
  display: none;
}
.step {
  position: relative;
  width: 900px;
  padding: 40px;
  margin: 20px auto;
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  -ms-box-sizing: border-box;
  -o-box-sizing: border-box;
  box-sizing: border-box;
  font-family: 'PT Serif', georgia, serif;
  font-size: 48px;
  line-height: 1.5;
}
.impress-enabled .step {
  margin: 0;
  opacity: 0.3;
  -webkit-transition: opacity 1s;
  -moz-transition: opacity 1s;
  -ms-transition: opacity 1s;
  -o-transition: opacity 1s;
  transition: opacity 1s;
}
.impress-enabled .step.active {
  opacity: 1;
}
.slide {
  display: block;
  width: 900px;
  height: 700px;
  padding: 40px 60px;
  background-color: #fff;
  border: 1px solid rgba(0, 0, 0, 0.3);
  border-radius: 10px;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
  color: #666;
  text-shadow: 0 2px 2px rgba(0, 0, 0, 0.1);
  font-family: 'Open Sans', Arial, sans-serif;
  font-size: 30px;
  line-height: 36px;
  letter-spacing: -1px;
}
.slide q {
  display: block;
  font-size: 50px;
  line-height: 72px;
  margin-top: 100px;
}
.slide q strong {
  white-space: nowrap;
}
#world {
  width: 1000px;
}
#title {
  padding: 0;
}
#title .try {
  font-size: 64px;
  position: absolute;
  top: -0.5em;
  left: 1.5em;
  -webkit-transform: translateZ(20px);
  -moz-transform: translateZ(20px);
  -ms-transform: translateZ(20px);
  -o-transform: translateZ(20px);
  transform: translateZ(20px);
}
#title h1 {
  font-size: 190px;
  -webkit-transform: translateZ(50px);
  -moz-transform: translateZ(50px);
  -ms-transform: translateZ(50px);
  -o-transform: translateZ(50px);
  transform: translateZ(50px);
}
#title .footnote {
  font-size: 32px;
}
#welcome {
  width: 600px;
  text-align: center;
  font-size: 40px;
  line-height: 1;
}
#welcome .title {
  font-size: 80px;
  line-height: 150px;
}
#welcome .footnote {
  font-size: 20px;
}
#tiny {
  width: 500px;
  text-align: center;
}
#me {
  width: 600px;
}
#me b {
  display: inline-block;
  -webkit-transition: 0.5s;
  -moz-transition: 0.5s;
  -ms-transition: 0.5s;
  -o-transition: 0.5s;
  transition: 0.5s;
}
#me.present .now_me {
  font-size: 90px;
  -webkit-transform: translateY(-10px);
  -moz-transform: translateY(-10px);
  -ms-transform: translateY(-10px);
  -o-transform: translateY(-10px);
  transform: translateY(-10px);
}
#me.present .unity {
  -webkit-transform: translateY(10px);
  -moz-transform: translateY(10px);
  -ms-transform: translateY(10px);
  -o-transform: translateY(10px);
  transform: translateY(10px);
  -webkit-transition-delay: 0.25s;
  -moz-transition-delay: 0.25s;
  -ms-transition-delay: 0.25s;
  -o-transition-delay: 0.25s;
  transition-delay: 0.25s;
}
#me.present .ios {
  -webkit-transform: translateY(10px);
  -moz-transform: translateY(10px);
  -ms-transform: translateY(10px);
  -o-transform: translateY(10px);
  transform: translateY(10px);
  -webkit-transition-delay: 0.5s;
  -moz-transition-delay: 0.5s;
  -ms-transition-delay: 0.5s;
  -o-transition-delay: 0.5s;
  transition-delay: 0.5s;
}
#me.present .photo {
  -webkit-transform: translateY(10px);
  -moz-transform: translateY(10px);
  -ms-transform: translateY(10px);
  -o-transform: translateY(10px);
  transform: translateY(10px);
  -webkit-transition-delay: 0.75s;
  -moz-transition-delay: 0.75s;
  -ms-transition-delay: 0.75s;
  -o-transition-delay: 0.75s;
  transition-delay: 0.75s;
}
#whereiam {
  width: 600px;
}
#whereiam .born {
  text-align: right;
  font-size: 30px;
}
#whereiam .study {
  text-align: right;
  font-size: 30px;
}
#whereiam .shanghai {
  color: #808080;
  font-size: 80px;
}
#whereiam .dongbei {
  color: #800080;
}
#japan {
  width: 800px;
}
#japan b {
  display: inline-block;
  -webkit-transition: 0.5s;
  -moz-transition: 0.5s;
  -ms-transition: 0.5s;
  -o-transition: 0.5s;
  transition: 0.5s;
}
#japan.present .trans {
  -webkit-transform: translateX(100px);
  -moz-transform: translateX(100px);
  -ms-transform: translateX(100px);
  -o-transform: translateX(100px);
  transform: translateX(100px);
  -webkit-transition-delay: 0.5s;
  -moz-transition-delay: 0.5s;
  -ms-transition-delay: 0.5s;
  -o-transition-delay: 0.5s;
  transition-delay: 0.5s;
}
#japan .footnote {
  font-size: 15px;
}
#meet {
  text-align: center;
}
#comein {
  width: 1000px;
  text-align: center;
}
#mywords {
  width: 1000px;
  text-align: center;
}
#grow {
  text-align: center;
}
#knowme {
  font-size: 45px;
}
#imagination {
  width: 600px;
}
#imagination .imagination {
  font-size: 78px;
}
#findme {
  width: 900px;
  padding-bottom: 120px;
  background-image: url('.././favicon.png');
  background-position: bottom right;
  background-repeat: no-repeat;
}
#prizeme p {
  -webkit-transform-style: preserve-3d;
  -moz-transform-style: preserve-3d; /* Y U need this Firefox?! */
  -ms-transform-style: preserve-3d;
  -o-transform-style: preserve-3d;
  transform-style: preserve-3d;
}
#prizeme span,
#prizeme b {
  display: inline-block;
  -webkit-transform: translateZ(40px);
  -moz-transform: translateZ(40px);
  -ms-transform: translateZ(40px);
  -o-transform: translateZ(40px);
  transform: translateZ(40px);
  -webkit-transition: 0.5s;
  -moz-transition: 0.5s;
  -ms-transition: 0.5s;
  -o-transition: 0.5s;
  transition: 0.5s;
}
#prizeme .horse {
  -webkit-transform: translateZ(-40px);
  -moz-transform: translateZ(-40px);
  -ms-transform: translateZ(-40px);
  -o-transform: translateZ(-40px);
  transform: translateZ(-40px);
}
#prizeme .moutain {
  -webkit-transform: translateZ(20px);
  -moz-transform: translateZ(20px);
  -ms-transform: translateZ(20px);
  -o-transform: translateZ(20px);
  transform: translateZ(20px);
}
#prizeme .hope {
  -webkit-transform: translateZ(-40px);
  -moz-transform: translateZ(-40px);
  -ms-transform: translateZ(-40px);
  -o-transform: translateZ(-40px);
  transform: translateZ(-40px);
}
#prizeme .youare {
  -webkit-transform: translateZ(60px);
  -moz-transform: translateZ(60px);
  -ms-transform: translateZ(60px);
  -o-transform: translateZ(60px);
  transform: translateZ(60px);
}
#prizeme .my {
  -webkit-transform: translateZ(-10px);
  -moz-transform: translateZ(-10px);
  -ms-transform: translateZ(-10px);
  -o-transform: translateZ(-10px);
  transform: translateZ(-10px);
}
#prizeme.present span,
#prizeme.present b {
  -webkit-transform: translateZ(0px);
  -moz-transform: translateZ(0px);
  -ms-transform: translateZ(0px);
  -o-transform: translateZ(0px);
  transform: translateZ(0px);
}
#subcribe .adv {
  font-size: 20px;
}
#motto {
  text-align: center;
}
#motto b {
  font-size: 70px;
  color: #8b0000;
  font-weight: bold;
}
#back {
  width: 700px;
  padding-bottom: 450px;
  /*background-image: url(.././favicon.png);*/
  background-position: bottom right;
  background-repeat: no-repeat;
}
#overview {
  display: none;
}
#tech .PHP {
  color: #008000;
}
#tech .Python {
  color: #800080;
}
#tech .Nodejs {
  color: #f00;
}
#knowme .title {
  font-size: 80px;
  line-height: 150px;
}
.impress-on-overview .step {
  opacity: 1;
  cursor: pointer;
}
.hint {
  display: none;
  position: fixed;
  left: 0;
  right: 0;
  bottom: 200px;
  background: rgba(0, 0, 0, 0.5);
  color: #eee;
  text-align: center;
  font-size: 50px;
  padding: 20px;
  z-index: 100;
  opacity: 0;
  -webkit-transform: translateY(400px);
  -moz-transform: translateY(400px);
  -ms-transform: translateY(400px);
  -o-transform: translateY(400px);
  transform: translateY(400px);
  -webkit-transition: opacity 1s, -webkit-transform 0.5s 1s;
  -moz-transition: opacity 1s, -moz-transform 0.5s 1s;
  -ms-transition: opacity 1s, -ms-transform 0.5s 1s;
  -o-transition: opacity 1s, -o-transform 0.5s 1s;
  transition: opacity 1s, transform 0.5s 1s;
}
.impress-enabled .hint {
  display: block;
}
.impress-on-welcome .hint {
  opacity: 1;
  -webkit-transform: translateY(0px);
  -moz-transform: translateY(0px);
  -ms-transform: translateY(0px);
  -o-transform: translateY(0px);
  transform: translateY(0px);
  -webkit-transition: opacity 1s 3s, -webkit-transform 0.5s 2.5s;
  -moz-transition: opacity 1s 3s, -moz-transform 0.5s 2.5s;
  -ms-transition: opacity 1s 3s, -ms-transform 0.5s 2.5s;
  -o-transition: opacity 1s 3s, -o-transform 0.5s 2.5s;
  transition: opacity 1s 3s, transform 0.5s 2.5s;
}
.impress-enabled {
  pointer-events: none;
}
.impress-enabled #impress {
  pointer-events: auto;
}

