embed,
figure,
figcaption,
footer,
header,
hgroup,
menu,
nav,
output,
ruby,
section,
summary,
time,
mark,
audio,
video {
  margin: 0;
  padding: 0;
  border: 0;
  font-size: 100%;
  font: inherit;
  vertical-align: baseline;
  line-height: 1.7em;
}
/* HTML5 display-role reset for older browsers */
article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
menu,
nav,
section {
  display: block;
}
body {
  line-height: 1;
}
ol,
ul {
  list-style: none;
}
blockquote,
q {
  quotes: none;
}
blockquote:before,
blockquote:after {
  content: '';
  content: none;
}
q:before,
q:after {
  content: '';
  content: none;
}
table {
  border-collapse: collapse;
  border-spacing: 0;
  width: 100%;
  table-layout: fixed;
}
table td:first-child {
  width: 3%;
  text-align: center;
}
body {
  width: 100%;
}
body:before,
body:after {
  content: '';
  display: table;
}
body:after {
  clear: both;
}
html,
body {
  height: 100%;
}
html {
  height: 100%;
  max-height: 100%;
}
body {
  font-family: 'ff-tisa-web-pro-1', 'ff-tisa-web-pro-2', 'Lucida Grande',
    'Hiragino Sans GB', 'Hiragino Sans GB W3', 'Microsoft YaHei',
    'WenQuanYi Micro Hei', sans-serif;
  font-size: 1em;
  color: #666;
  -webkit-font-smoothing: antialiased;
}
::selection {
  background: #4e97d8;
  color: #fff;
}
::-moz-selection {
  background: #4e97d8;
  color: #fff;
}
a {
  text-decoration: none;
  color: #458fd2;
}
a:hover {
  color: #1461a7;
  -o-transition: 0.5s;
  -ms-transition: 0.5s;
  -moz-transition: 0.5s;
  -webkit-transition: 0.5s;
}
h1,
h2,
h3,
h4,
h5 {
  margin-top: 1em;
  margin-bottom: 0.5em;
  font-family: 'ff-tisa-web-pro-1', 'ff-tisa-web-pro-2', 'Lucida Grande',
    'Hiragino Sans GB', 'Hiragino Sans GB W3', 'Microsoft YaHei',
    'WenQuanYi Micro Hei', sans-serif;
  font-weight: lighter;
  color: #333;
  -webkit-font-smoothing: antialiased;
}
h1 {
  margin-top: 0;
  font-size: 2.4em;
  line-height: 1.2em;
  letter-spacing: 0.05em;
}
h2 {
  font-size: 2em;
}
h3 {
  font-size: 1.6em;
}
h4 {
  font-size: 1.2em;
}
h4 {
  font-size: 1.1em;
}
h5 {
  font-size: 1em;
}
p {
  line-height: 1.7em;
}
strong {
  font-weight: bold;
}
em {
  font-style: italic;
}
blockquote {
  margin: 1em 0;
  padding: 1em 1.25em;
  /*background: #f8f8f8;  rgba(37,104,163,0.1) */
  /*border: 1px solid #eeeeee;*/
  border-left: 3px solid #25688f;
  /*border-radius: 3px;*/
  font-family: 'ff-tisa-web-pro-1', 'ff-tisa-web-pro-2', 'Lucida Grande',
    'Hiragino Sans GB', 'Hiragino Sans GB W3', 'Microsoft YaHei',
    'WenQuanYi Micro Hei', sans-serif;
  font-weight: lighter;
  font-size: 1em;
  background-color: #f7f7f9;
  /*text-align: center; */
}
blockquote p:last-child {
  margin-bottom: 0;
}
ol,
ul {
  margin: 0 0 1.3em 2.5em;
}
ol li,
ul li {
  margin: 0 0 0.2em 0;
  line-height: 1.6em;
}
ol ol,
ol ul,
ul ol,
ul ul {
  margin: 0.1em 0 0.2em 2em;
}
ol {
  list-style-type: decimal;
}
ul {
  list-style-type: disc;
}
code {
  padding: 0.1em 0.4em;
  background: #f2f8ff;
  border: 1px solid #edf6fd;
  border-radius: 3px;
  font-family: Consolas, Menlo, Monaco, Lucida Console, Liberation Mono,
    DejaVu Sans Mono, Bitstream Vera Sans Mono, Courier New, monospace, serif;
  font-size: 0.9em;
  vertical-align: bottom;
  word-wrap: break-word;
}
pre {
  margin-bottom: 1.3em;
  padding: 1em 2.5%;
  background: #f2f8ff;
  border: 1px solid #edf6fd;
  border-radius: 3px;
  font-family: Consolas, Menlo, Monaco, Lucida Console, Liberation Mono,
    DejaVu Sans Mono, Bitstream Vera Sans Mono, Courier New, monospace, serif;
  font-size: 0.9em;
  font-weight: normal;
  line-height: 1.3em;
  overflow: scroll;
}
pre code {
  padding: 0;
  background: none;
  border: none;
  word-wrap: normal;
}
.date,
.time,
.author,
.tags {
  font-size: 0.8em;
  color: #c7c7c7;
}
.date a,
.time a,
.author a,
.tags a {
  color: #666;
}
.date a:hover,
.time a:hover,
.author a:hover,
.tags a:hover {
  color: #458fd2;
}
.excerpt {
  margin: 0;
  font-size: 0.9em;
  color: #999;
}
.intro {
  font-family: 'ff-tisa-web-pro-1', 'ff-tisa-web-pro-2', 'Lucida Grande',
    'Hiragino Sans GB', 'Hiragino Sans GB W3', 'Microsoft YaHei',
    'WenQuanYi Micro Hei', sans-serif;
  font-size: 1.2em;
  font-weight: lighter;
  color: #999;
}
.block-heading {
  display: inline;
  float: left;
  width: 940px;
  margin: 0 10px;
  position: relative;
  bottom: -15px;
  font-size: 0.8em;
  font-weight: bold;
  text-align: center;
  text-transform: uppercase;
  letter-spacing: 1px;
}
.label {
  position: relative;
  display: inline-block;
  padding: 8px 18px 9px 18px;
  background: #4e97d8;
  border-radius: 3px;
  text-align: center;
  color: #fff;
}
.container {
  position: relative;
  z-index: 500;
  width: 940px;
  margin: 0 auto;
}
.content-wrapper {
  z-index: 800;
  width: 70%;
  margin-left: 27%;
}
.content-wrapper__inner {
  margin: 0 7%;
  padding: 50px 0;
}
.footer {
  display: block;
  padding: 2em 0 0 0;
  border-top: 2px solid #ddd;
  font-size: 0.7em;
  color: #b3b3b3;
}
.footer__copyright {
  display: block;
  margin-bottom: 0.7em;
}
.footer__copyright a {
  color: #a6a6a6;
  text-decoration: underline;
}
.footer__copyright a:hover {
  color: #458fd2;
}
.avatar,
.logo {
  border-radius: 50%;
  border: 3px solid #fff;
  box-shadow: 0 0 1px 1px rgba(0, 0, 0, 0.3);
}
hr {
  border: none;
}
.section-title__divider {
  width: 30%;
  margin: 2.2em 0 2.1em 0;
  border-top: 1px solid #ddd;
}
.hidden {
  display: none !important;
}
.post-comments {
  border-top: 1px solid #ddd;
  padding: 60px 0;
}
.post-meta {
  margin: 0 0 0.4em 0;
  color: #c7c7c7;
}
.post-meta__date {
  margin-right: 0.5em;
}
.post-meta__tags {
  margin-left: 0.4em;
}
.post-meta__author {
  margin-left: 1.5em;
}
.post-meta__avatar {
  display: inline-block;
  width: 22px;
  height: 22px;
  margin: 0 0.3em -0.4em 0;
  border: none;
  box-shadow: none;
}
.post-meta .page-pv {
  margin-right: 0.5em;
  font-size: 0.8em;
  float: right;
}
i.fa.fa-spinner.fa-spin {
  font-size: 0.8em;
}
.post img {
  max-width: 100%;
  margin: 0 auto;
  border-radius: 3px;
  text-align: center;
  display: block;
}
.post pre {
  width: 95%;
  overflow: auto;
}
.post hr {
  display: block;
  width: 30%;
  margin: 2em 0;
  border-top: 1px solid #ddd;
}
.panel {
  display: table;
  width: 100%;
  height: 100%;
}
.panel__vertical {
  display: table-cell;
  vertical-align: middle;
}
.panel-title {
  margin: 0 0 5px 0;
  font-size: 2.5em;
  letter-spacing: 4px;
  color: #fff;
}
.panel-subtitle {
  font-family: 'ff-tisa-web-pro-1', 'ff-tisa-web-pro-2', 'Lucida Grande',
    'Hiragino Sans GB', 'Hiragino Sans GB W3', 'Microsoft YaHei',
    'WenQuanYi Micro Hei', sans-serif;
  font-size: 1.2em;
  font-weight: lighter;
  letter-spacing: 3px;
  color: #ccc;
  -webkit-font-smoothing: antialiased;
}
.panel-cover {
  display: block;
  position: fixed;
  z-index: 900;
  width: 100%;
  max-width: none;
  height: 100%;
  background: url('../images/background-cover.jpg') top left no-repeat #666;
  background-size: cover;
}
.panel-cover--collapsed {
  width: 24%;
  max-width: 500px;
}
.panel-cover--overlay {
  display: block;
  position: absolute;
  z-index: 0;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-color: rgba(68, 68, 68, 0.6);
  background-image: -webkit-linear-gradient(
    -410deg,
    rgba(68, 68, 68, 0.6) 20%,
    rgba(0, 0, 0, 0.9)
  );
  background-image: linear-gradient(
    140deg,
    rgba(68, 68, 68, 0.6) 20%,
    rgba(0, 0, 0, 0.9)
  );
}
.panel-cover__logo {
  margin-bottom: 0.2em;
}
.panel-cover__description {
  margin: 0 30px;
}
.panel-cover__divider {
  width: 50%;
  margin: 20px auto;
  border-top: 1px solid rgba(255, 255, 255, 0.14);
}
.panel-cover__divider--secondary {
  width: 15%;
}
.panel-main {
  display: table;
  width: 100%;
  height: 100%;
}
.no-js .panel-main {
  width: 30%;
  max-width: 500px;
}
.panel-main__inner {
  display: table-cell;
  vertical-align: middle;
  position: relative;
  z-index: 800;
  padding: 0 60px;
}
.panel-main__content {
  max-width: 620px;
  margin: 0 auto;
}
.panel-main__content--fixed {
  width: 480px;
  transition: width 1s;
  -webkit-transition: width 1s;
  /* Safari */
}
.panel-inverted {
  font-weight: 100;
  text-align: center;
  color: #fff;
  text-shadow: 0 1px 1px rgba(0, 0, 0, 0.4);
}
.panel-inverted a {
  color: #fff;
}
.cover-navigation {
  margin-top: 42px;
}
.cover-navigation--social {
  margin-left: 30px;
}
.cover-blue {
