  background-image: -webkit-linear-gradient(
    -410deg,
    rgba(61, 66, 96, 0.6) 20%,
    rgba(21, 23, 34, 0.8)
  );
  background-image: linear-gradient(
    140deg,
    rgba(61, 66, 96, 0.6) 20%,
    rgba(21, 23, 34, 0.8)
  );
}
.cover-disabled {
  background: none;
}
.btn,
.navigation__item a {
  padding: 10px 20px;
  border: 1px solid #4e97d8;
  border-radius: 20px;
  font-size: 0.9em;
  font-weight: bold;
  letter-spacing: 1px;
  text-shadow: none;
  color: #4e97d8;
  -webkit-font-smoothing: antialiased;
}
.btn:hover,
.navigation__item a:hover {
  color: #458fd2;
  border-color: #458fd2;
}
.btn-border-small {
  border: 1px solid #4e97d8;
  border-radius: 20px;
  padding: 6px 8px;
  font-size: 0.8em;
  margin-left: 10px;
}
.btn-secondary {
  border-color: #5ba4e5;
  color: #5ba4e5;
}
.btn-secondary:hover {
  color: #217fd2;
  border-color: #217fd2;
}
.btn-tertiary {
  border-color: #999;
  color: #999;
}
.btn-tertiary:hover {
  color: #737373;
  border-color: #737373;
}
.btn-large {
  padding: 10px 24px;
  font-size: 1.1em;
}
.btn-small {
  padding: 8px 12px;
  font-size: 0.7em;
}
.btn-mobile-menu {
  display: none;
  position: fixed;
  z-index: 9999;
  top: 0;
  right: 0;
  left: 0;
  width: 100%;
  height: 35px;
  background: rgba(51, 51, 51, 0.98);
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  text-align: center;
}
.btn-mobile-menu__icon,
.btn-mobile-close__icon {
  position: relative;
  top: 10px;
  color: #fff;
}
nav {
  display: inline-block;
  position: relative;
}
.navigation {
  display: inline-block;
  float: left;
  position: relative;
  margin: 0;
  list-style-type: none;
}
.navigation__item {
  display: inline-block;
  margin: 5px 1px 0 0;
  line-height: 1em;
}
.navigation__item a {
  display: block;
  position: relative;
  border-color: #fff;
  color: #fff;
  opacity: 0.8;
}
.navigation__item a:hover {
  color: #fff;
  border-color: #fff;
  opacity: 1;
}
.navigation--social a {
  border: 0px;
  padding: 6px 8px 6px 9px;
}
.navigation--social a .label {
  display: none;
}
.navigation--social a .icon {
  display: block;
  font-size: 1.7em;
}
.pagination {
  display: block;
  margin: 0 0 4em 0;
}
.pagination__page-number {
  margin: 0;
  font-size: 0.8em;
  color: #999;
}
.pagination__newer {
  margin-right: 1em;
}
.pagination__older {
  margin-left: 1em;
}
i {
  font-family: 'entypo';
  font-weight: normal;
  font-style: normal;
  font-size: 18px;
}
.social {
  font-size: 22px;
}
.icon-social {
  font-family: 'entypo-social';
  font-size: 22px;
  display: block;
  position: relative;
}
.post-list {
  margin: 0;
  padding: 0;
  list-style-type: none;
  text-align: left;
}
.post-list li {
  margin: 0 0 2.2em 0;
}
.post-list li:last-child hr {
  display: none;
}
.post-list__post-title {
  margin-top: 0;
  margin-bottom: 0.2em;
  font-size: 1.5em;
  line-height: 1.3em;
}
.post-list__post-title a {
  color: #333;
}
.post-list__post-title a:hover {
  color: #458fd2;
}
.post-list__meta {
  display: block;
  margin: 0.7em 0 0 0;
  font-size: 0.9em;
  color: #c7c7c7;
}
.post-list__meta--date {
  margin-right: 0.5em;
  color: #c7c7c7;
}
.post-list__meta--tags {
  margin-left: 0.5em;
}
.post-list__divider {
  width: 30%;
  margin: 2.2em 0 2.1em 0;
  border-top: 1px solid #ddd;
}
*:focus {
  outline: none;
}
.read-more {
  margin-top: 1em;
  margin-bottom: 1em;
  padding-top: 1.2em;
  padding-bottom: 1em;
  border-top: 1px solid #ddd;
}
.read-more-item {
  display: inline-block;
  vertical-align: top;
  width: 48%;
}
.read-more-item-dim {
  color: #4e97d8;
  font-size: 0.8em;
  line-height: 1.8em;
}
