$(document).ready(function () {
  $('body').removeClass('no-js')

  $('a.blog-button').click(function () {
    if ($('.panel-cover').hasClass('panel-cover--collapsed')) return
    currentWidth = $('.panel-cover').width()
    if (currentWidth < 960) {
